import React, { useState } from 'react'
import './Login.css'
// import 'antd/dist/antd.css';
// import { Form, Input, Button, Checkbox } from 'antd';
import { Link } from 'react-router-dom';
import logo from '../img/logo.png';


function Login() {

    const [idval, setidval] = useState("");
    const [pwval, setpwval] = useState("");

    const handlesubmit = (event) => {
        event.preventDefualt();
    }

    return (
        <div className="main-login">
            <div className="login-contain">
                <div className="login-content">
                    <div >
                    <div className="login-header img-class">
                        <img src={logo} id="img-id" alt="로고이미지" srcset="" />
                    </div>
                    <div className="login-header txt">
                        <h3 style={{color:'dimgray', fontWeight:'bold', fontSize}}>지금 에브리데이를 시작해보세요!</h3>
                    </div>
                    </div>
                    <form onSubmit={handlesubmit}>
                        {/* <label for="id">ID</label> */}
                        <input type="id" id="id" placeholder="아이디"
                            value={idval} onChange={(e) => { setidval(e.target.value) }} />
                        {/* <label for="pw">Password</label> */}
                        <input type="password" id="pw" placeholder="비밀번호"
                            value={pwval} onChange={(e) => setpwval(e.target.value)} />
                        <button type="submit" className="login_btn">로그인</button>
                    </form>


                    <div>
                        <h4 style={{color:'gray'}}>에브리데이에 처음이신가요? <Link className="link" to='/register'>회원가입</Link></h4>
                    </div>
                </div>


            </div>
        </div>

    )
}

